import React, { useState } from 'react';
import './book-list.css';
import {Button} from 'reactstrap';
import AddReviewModal from '../../Utlies/add-review-modals';


export default function(){

    const [showModal, setShowModal] = useState(false);

    function showAddReviewModal(value){
        setShowModal(value);
    }

    function updateReview(){


    }

    return (
        <div>
            <div className="row header-row">
                <div className="col-xs-4 col-sm-4 col-md-4 col-lg-4 header-text"><h5>Books</h5> </div>
                <div className="col-xs-4 col-sm-4 col-md-4 col-lg-4"> </div>
                <div className="col-xs-3 col-sm-4 col-md-4 col-lg-4">
                    <div className="btn-container"></div><Button onClick={e=>showAddReviewModal(true)}><i className="fas fa-plus"></i> Add review</Button>
                </div>
                
            </div>

            <div className="row">
                <div className="row book-container">
                    <div className="">
                        <div className="book-title">
                            <h5>Books</h5>
                        </div>
                    </div>
                    <div className="row"></div>
                    <div className="row"></div>
                   
                    
                </div>
            </div>

            <div className="row">
                <div className=" book-container">
                    <h5>Books</h5>
                </div>
            </div>

            <div className="row">
                <div className="book-container">
                    <h5>Books</h5>
                </div>
            </div>

            {showModal ? <AddReviewModal closeModal={showAddReviewModal} updateReview={updateReview}/> : null}
        
        </div>
    );
}
