import { useMutation, useQuery } from "@apollo/client";
import React, { useState, useEffect, useContext } from "react";
import { ADD_USER, GET_USER } from "../../gql-helper/queries";
import "./sign-up-form.css";

export default function SignUp(props) {
  const [username, setUsername] = useState(""),
    [email, setEmail] = useState(""),
    [password, setPassword] = useState(""),
    [retypedPassword, setRetypedPassword] = useState(""),
    [userNameReq, setUserNameReq] = useState(false),
    [emailReq, setEmailReq] = useState(false),
    [passwordReq, setPasswordReq] = useState(false),
    [retypedPasswordReq, setRetypedPasswordReq] = useState(false),
    [userAlreadyExists, setUserAlreadyExists] = useState(false),
    [passwordMismatched, setPasswordMismatched] = useState(false);

  const [addUser] = useMutation(ADD_USER),
    { loading, data, refetch } = useQuery(GET_USER, {
      variables: { email: email },
    });

  const isFormValidated = () => {
    if (!username) {
      setUserNameReq(true);
      return false;
    }
    if (!email) {
      setEmailReq(true);
      return false;
    }
    if (!password) {
      setPasswordReq(true);
      return false;
    }
    if (!retypedPassword) {
      setRetypedPasswordReq(true);
      return false;
    }

    if (password !== retypedPassword) {
      setPasswordMismatched(false);
      return false;
    }

    return true;
  };

  useEffect(() => {
    console.log("email before refecth", email);
    refetch({ email });

    console.log("data in useEffect",data);
    if (data && data.users && data.users.length>0) setUserAlreadyExists(true); 

    return () => {};
  }, [email, data]);

  return (
    <div id="login-box">
      <div className="left">
        {/* 
      <div style={{marginBottom: "20px"}}>
        <h1>Sign up</h1> <span style={{float:"right",marginTop: "-47px",cursor:"pointer"}}><i className="fa fa-times"></i></span>
      </div> */}

        <input
          type="text"
          name="username"
          placeholder="Username"
          className="signupInput"
          onChange={(e) => setUsername(e.target.value)}
        />
        <input
          type="email"
          name="email"
          placeholder="E-mail"
          className="signupInput"
          onChange={(e) => setEmail(e.target.value)}
        />
        {userAlreadyExists ? <span className="error-class">User already exists by this email.</span> :null}
        <input
          type="password"
          name="password"
          placeholder="Password"
          className="signupInput"
          onChange={(e) => setPassword(e.target.value)}
        />
        <input
          type="password"
          name="password2"
          placeholder="Retype password"
          className="signupInput"
          onChange={(e) => setRetypedPassword(e.target.value)}
        />

        <input
          type="submit"
          name="signup_submit"
          value="Sign me up"
          onClick={(e) =>
            isFormValidated()
              ? addUser({
                  variables: {
                    name: username,
                    email: email,
                    password: password,
                  },
                })
              : false
          }
        />
      </div>

      {/* <div className="right">
      <span className="loginwith">Sign in with<br />social network</span>
      
      <button className="social-signin facebook">Log in with facebook</button>
      <button className="social-signin twitter">Log in with Twitter</button>
      <button className="social-signin google">Log in with Google+</button>
    </div> */}
      {/* <div className="or">OR</div> */}
    </div>
  );
}
