import React, { useState } from 'react';
import './dropdown.css'


const Example = () => {

    
    function myFunction() {
       if(document.getElementById("myDropdown")) document.getElementById("myDropdown").classList.toggle("show");
      }
      
      function filterFunction() {
        var input, filter, ul, li, a, i;
         input =  document.getElementById("myInput") ? document.getElementById("myInput") : '';
         filter = input ? input.value.toUpperCase() : '';
        let div = document.getElementById("myDropdown") ? document.getElementById("myDropdown") : '';
         a = div ? div.getElementsByTagName("a"): '';
        for (i = 0; i < a.length; i++) {
            let txtValue = a[i].textContent || a[i].innerText;
          if (txtValue.toUpperCase().indexOf(filter) > -1) {
            if(a[i])  a[i].style.display = "";
          } else {
            if(a[i]) a[i].style.display = "none";
          }
        }
      }

    return(
        <>
            <div className="dropdown">
            <button onClick={e =>myFunction()} className="dropbtn">Select author</button>
    <div id="myDropdown" className="dropdown-content">
        <input type="text" placeholder="Search.." id="myInput" onKeyUp={e =>filterFunction()}/>
        <a href="#about">About</a>
        <a href="#base">Base</a>
        <a href="#blog">Blog</a>
        <a href="#contact">Contact</a>
        <a href="#custom">Custom</a>
        <a href="#support">Support</a>
        <a href="#tools">Tools</a>
    </div>
    </div>

        </>
    )
}

export default Example;
