import React, { useState } from 'react';
import { Button, Modal, ModalHeader, ModalBody, ModalFooter, InputGroup, Input} from 'reactstrap';
import styles from './add-review-modal.css';

const AddReviewModal = (props) => {
  const {
    buttonLabel,
    className
  } = props;

  const [modal, setModal] = useState(true);
  const [title, setTitle] = useState(true);
  const [author, setAuthor] = useState(true);
  const [desc, setDesc] = useState(true);

  const toggle = () => setModal(!modal);

  function addReview(){

    closeModal();

  }

  function closeModal(){
    setModal(!modal);

    if(props.closeModal) {
    
        setTimeout(()=>{
            props.closeModal(false);
        },100);    
    }
  }

  return (
    <div>
      
      <Modal isOpen={modal} toggle={toggle} className={className} onClosed={()=>closeModal()}>
        <ModalHeader toggle={toggle}>Add review</ModalHeader>
        <ModalBody>
            <div>
                <div className="form-field">
                    Title: <Input placeholder="add a title" onChange={e=>{setTitle(e.target.value)}}/>
                </div>

                <div className="form-field">
                    Author: <Input placeholder="add authorName" onChange={e=>{setAuthor(e.target.value)}} />
                </div>

                <div className="form-field">
                    How was it: <textarea placeholder="Short summary" style={{width:"100%"}} onChange={e=>{setDesc(e.target.value)}}/>
                </div>
              
              
              
            </div>
        </ModalBody>
        <ModalFooter>
            
          <Button color="secondary" onClick={e=>addReview()}><i class="fas fa-plus"></i> Add</Button>
        </ModalFooter>
      </Modal>
    </div>
  );
}

export default AddReviewModal;
