import { useMutation, useQuery } from "@apollo/client";
import React, { useState, useEffect } from "react";
import { LOGIN_USER } from "../../gql-helper/queries";
import "./sign-up-form.css";

export default function Login(props) {
  const { closeModal } = props,
    [email, setEmail] = useState(""),
    [password, setPassword] = useState(""),
    [emailReq, setEmailReq] = useState(false),
    [passwordReq, setPasswordReq] = useState(false),
    [ewmailPasswordMismatched, setEmailPasswordMismatched] = useState(false);

  const [loginUser] = useMutation(LOGIN_USER),
    { data, refetch } = useQuery(GET_USER, {
      variables: { email: email },
    });

  const isFormValidated = () => {
  
    if (!email) {
      setEmailReq(true);
      return false;
    }
    if (!password) {
      setPasswordReq(true);
      return false;
    }

    return true;
  };

  const validateAndLogin = async()=>{

    if(isFormValidated()){
      await loginUser({
        variables: {
          email: email,
          password: password,
        }
      });
      closeParentModal();
    }
  }

  const closeParentModal = () => (closeModal ? closeModal() : false);

  useEffect(() => {
    refetch({ email });

    return () => {};
  }, [email, data]);

  return (
    <div id="login-box">
      <div className="left">
        <input
          type="email"
          name="email"
          placeholder="E-mail"
          className="signupInput"
          onChange={(e) => {
            setEmailReq(false);
            setEmail(e.target.value);
          }}
        />
        {emailReq ? (
          <span className="error-class">*Email required.</span>
        ) : null}
        <input
          type="password"
          name="password"
          placeholder="Password"
          className="signupInput"
          onChange={(e) => {
            setPasswordReq(false);
            setPassword(e.target.value);
          }}
        />
        {passwordReq ? (
          <span className="error-class">*Password required.</span>
        ) : null}

        <input
          type="submit"
          name="signup_submit"
          value="Log in"
          onClick={(e) =>validateAndLogin()}
        />
      </div>
    </div>
  );
}
