import React, { useState, useEffect } from 'react';
import './App.css';
import {
  InputGroup,
  InputGroupAddon,
  Input,
  Button,
 } from 'reactstrap';

 import BookSection from './components/books-section/index';
 import Navbar from './components/navbar/navbar';


function App() {


  useEffect(() => {

  const script = document.createElement('script');
  script.src = "./src/packages/dropzone-5.7.0/dist/dropzone.js.map";
  script.async = true;
  document.body.appendChild(script);
  return () => {
    document.body.removeChild(script);
  }

  }, [])
  return (
    <div className="App">
      <div className="Navbar">
        <Navbar/>
      </div>
      <header className="App-header">
        <div className="container">
          <div className="search-section">
              {/* <span><h2>Search books</h2></span> */}
              <span>
                <InputGroup>
                  <Input placeholder="Search book by title" size="lg"/>
                  <InputGroupAddon addonType="append"><Button> <i className="fas fa-search"></i></Button></InputGroupAddon>

                </InputGroup>
              </span>
            </div>
        </div>


      </header>
      <BookSection/>
    </div>
  );
}

export default App;
