//   }
// `;

function App() {

  // const {loading, error, data} = useQuery(TEST_QUERY);

  // console.log("loading",loading);
  // console.log("error",error);
  // console.log("data",data);

  return (
    <div className="App">
      <div className="Navbar"></div>
      <header className="App-header">
        <div className="search-section">
          {/* <span><h2>Search books</h2></span> */}
          <span>
            <InputGroup>
              <Input placeholder="Search book by title"/>
              <InputGroupAddon addonType="append"><Button> <i className="fas fa-search"></i></Button></InputGroupAddon>

            </InputGroup>
          </span>
        </div>

      </header>
      <BookSection/>
    </div>
  );
}

export default App;
