import React, { useState } from 'react';
import { Dropdown, DropdownMenu, DropdownToggle } from 'reactstrap';
import './dropdown.css'

const Example = () => {
  const [dropdownOpen, setDropdownOpen] = useState(false);
  const [testValue, setTestValue] = useState('');
  const [menu,setMenu] = useState(["Value oo","vasdkaf"]);

  const toggle = () => setDropdownOpen(prevState => !prevState);

  return (
    <Dropdown isOpen={dropdownOpen} toggle={toggle}>
      <DropdownToggle
        tag="span"
        data-toggle="dropdown"
        aria-expanded={dropdownOpen} 
      >
         
    <input  className="dropdown-box" value={testValue} placeholder="please select"/>   
        
       
      </DropdownToggle>
      <DropdownMenu>
  {menu.map((value)=><div className="drop-menu" onClick={e=>{console.log(e.target.value); setDropdownOpen(false); setTestValue(e.target.value)}}>{value}</div>)}
        
      </DropdownMenu>
    </Dropdown>
  );
}

export default Example;
