import React, { useState, useEffect } from "react";
import {
  Modal,
  ModalBody,
  ModalHeader,
} from "reactstrap";

import SignupModal from "../../../components/sign-up/sign-in-form";

export default function LoginSignUpModal(props) {
  const { className, showModal } = props;

  const [modal, setModal] = useState(true);

  const toggle = () => setModal(!modal);

//   useEffect(()=>{

//     if(showModal) setModal(true)

//   },[showModal]);

  function closeModal() {
    setModal(!modal);

    if (props.closeModal) {
      setTimeout(() => {
        props.closeModal(false);
      }, 100);
    }
  }

  return (
    <div>
      <Modal
        size="md"
        isOpen={modal}
        toggle={toggle}
        className={className}
        
        onClosed={() => closeModal()}
      >
        <ModalHeader toggle={toggle} onClosed={() => closeModal()} style={{marginLeft:"10px"}}>
        <h1>Sign up</h1>
        </ModalHeader>

    <ModalBody style={{padding:"0!important"}}>
        <SignupModal />
      </ModalBody>
      </Modal>
      
    </div>
  );
}
