import React, { useState, useEffect, useContext } from "react";
import { Button, Popover, PopoverHeader, PopoverBody } from "reactstrap";
import { FilterContext } from "../../context-helper/context-helper";

const CustomPopover = (props) => {
  const { targetId, title, body } = props;
  const {showPopover, setShowPopover} = useContext(FilterContext);
  const [popoverOpen, setPopoverOpen] = useState(false);

  const toggle = () => setPopoverOpen(!popoverOpen);

  useEffect(() => {
    if (showPopover) toggle();
  }, [showPopover]);

 

  //hide Popover On Outside Click   
  document.body.onclick = function(e) {
    const clickedEl = e.target,
    popoverClassList = ["popover-header","popover-body","acc-option-popover"]

    if(clickedEl && !popoverClassList.includes(clickedEl.className)) setPopoverOpen(false);
    // if(clickedEl && clickedEl.className === "acc-option-popover") {
    //   setTimeout(()=>{
    //     setPopoverOpen(false);
    //   },900)
    // }
}

  return (
    <div>
      <Popover
        placement="bottom"
        id="popover-container"
        isOpen={popoverOpen}
        target={targetId}
        toggle={toggle}
        style={{ width: "150px" }}
      >
        <PopoverHeader style={{ textAlign: "center" }}>{title}</PopoverHeader>
        <PopoverBody>{body}</PopoverBody>
      </Popover>
    </div>
  );
};

export default CustomPopover;
