import React, { useState, useEffect } from "react";
import "./dropdown.css";

const CustomDropdown = (props) => {

  const { context, placeholder } = props;
  const [menus, setMenus] = useState([]);
  const [selectedItem, setSelectedItem] = useState(placeholder);

  useEffect(() => {
    if (props.menus) {

      let propsMenu =props.menus ? props.menus : [],
      updatedList = [];

      if(context === 'authors') {

        //update menu list in just text,value
        let updatedAuthorsList =  propsMenu.map((author)=> {
          return {
            text: author.name,
            value:author._id
          }
        });

        console.log("updatedAuthorsList ",updatedAuthorsList);
        updatedList = updatedAuthorsList;
      
      } else if(context === 'authors') {
        //update menu list in just text,value
        let updatedGenreList =  propsMenu.map((genre)=> {
          return {
            text: genre,
            value:genre
          }
        });

        updatedList = updatedGenreList;
      }

      setMenus(updatedList);
    }

    return () => {};
  }, [props.menus]);

  function showDropdown() {
    if (document.getElementById("myDropdown"))
      document.getElementById("myDropdown").classList.toggle("show");
  }

  function filterFunction(value) {
    var input, filter, ul, li, a, i;
    input = document.getElementById("myInput")
      ? document.getElementById("myInput")
      : "";
    filter = input ? input.value.toUpperCase() : "";
    let div = document.getElementById("myDropdown")
      ? document.getElementById("myDropdown")
      : "";
    a = div ? div.getElementsByTagName("a") : "";
    for (i = 0; i < a.length; i++) {
      let txtValue = a[i].textContent || a[i].innerText;
      if (txtValue.toUpperCase().indexOf(filter) > -1) {
        if (a[i]) a[i].style.display = "";
      } else {
        if (a[i]) a[i].style.display = "none";
      }
    }
  }

  function setSelectedValue(selectedItem){

    let selectedText =selectedItem.text ? selectedItem.text : '',
      selecetedValue = selectedItem.value ? selectedItem.value : '';


    if(selectedText){
      
      setSelectedItem(selectedText);

      if(context === 'authors' && props.setParentsAuthor) props.setParentsAuthor(selectedText,selecetedValue);
      if(context === 'genre' && props.setParentsGenre) props.setParentsAuthor(selectedText,selecetedValue);
    }

    showDropdown();
    
  }

  return (
    <>
      <div className="dropdown">
        <button onClick={(e) => showDropdown()} className="dropbtn">
           {selectedItem}
        </button>
        <div id="myDropdown" className="dropdown-content">
          <input
            type="text"
            placeholder="Search.."
            id="myInput"
            onKeyUp={(e) => filterFunction(e.target.value)}
          />

          {menus.map((menu, index) => (
            <a
              key={index}
              href="#about"
              className="menu"
              onClick={(e) => setSelectedValue(menu)}
            >
              {menu.text}
            </a>
          ))}
        </div>
      </div>
    </>
  );
};

export default CustomDropdown;
