import React, { useState, useEffect } from "react";
import {
  Button,
  Modal,
  ModalHeader,
  ModalBody,
  ModalFooter,
  Input,
  Tooltip,
} from "reactstrap";
import "./add-review-modal.css";
import { useMutation, useQuery } from "@apollo/client";
import CustomDropdown from "./dropdown/dropdown";
import CustomGenreDropdown from "./dropdown/dropdown";

import {
  ADD_REVIEW,
  ADD_AUTHOR,
  AUTHORS,
  GET_REVIEW,
} from "../gql-helper/queries";

const AddReviewModal = (props) => {
  const { className } = props;

  const [modal, setModal] = useState(true);
  const [title, setTitle] = useState("");
  const [author, setAuthor] = useState("");
  const [genre, setGenre] = useState("");
  const [desc, setDesc] = useState(true);
  const [showDropdown, setShowDropdown] = useState(false);
  const [showAddNewAurthor, setShowAddNewAurthor] = useState(false);
  const [readOnly, setReadOnly] = useState(false);
  const [authorsList, setAuthorsList] = useState([]);
  const [selectedAuthor, setSelectedAuthor] = useState("");
  const [hasAllMandatoryFields, setHasAllMandatoryFields] = useState(true);
  const [titleReq, setTitleReq] = useState(true);
  const [authorReq, setAuthorReq] = useState(true);

  const [addTooltipOpen, setAddTooltipOpen] = useState(false);
  const [backTooltipOpen, setBackTooltipOpen] = useState(false);

  const addToggleTooltip = () => setAddTooltipOpen(!addTooltipOpen);
  const backToggleTooltip = () => setBackTooltipOpen(!backTooltipOpen);

  const [addReview, { reviewData }] = useMutation(ADD_REVIEW);
  const [addAuthor, { authordata }] = useMutation(ADD_AUTHOR);
  const { error, loading, data } = useQuery(AUTHORS);

  const genreList = [
    "Action and adventure",
    "Bio-graphy",
    "Children's",
    "Graphic novel",
    "Poetry",
    "Political thriller",
    "Science fiction"
  ];

  const toggle = () => setModal(!modal);

  function setAuthorFromDropdown(text, value) {
    setSelectedAuthor(value);

  }

  function setGenreFromDropdown(text, value){
    setGenre(value);
    
    console.log("value come from dropdown ", value);
    console.log("Author id after selected ", selectedAuthor);
  }

  function closeModal() {
    setModal(!modal);

    if (props.closeModal) {
      setTimeout(() => {
        props.closeModal(false);
      }, 100);
    }
  }

  // var myDropzone = new Dropzone("div#myId", { url: "/file/post"});

  useEffect(() => {
    if (props.updateReview) props.updateReview();

    if (data && data.authors && data.authors.length > 0) {
      setAuthorsList(data.authors);
    }

    //form validation
    if (title.length > 0 && selectedAuthor.length > 0) {
      setHasAllMandatoryFields(false);
    }

    if(title) setTitleReq(false);
    if(selectedAuthor) setAuthorReq(false);

    return () => {};
  }, [data, title, selectedAuthor]);

  return (
    <div>
      <Modal
        isOpen={modal}
        toggle={toggle}
        className={className}
        onClosed={() => closeModal()}
      >
        <ModalHeader toggle={toggle} style={{ backgroundColor: "#f7f7f7" }}>
          Add review
        </ModalHeader>
        <ModalBody>

        <button 
      className="dropzone"
      id="my-awesome-dropzone"></button>
          <div>
            <div className="form-field">
              Title:{" "}
              <Input
                placeholder="add a title"
                onChange={(e) => {
                  setTitle(e.target.value);
                }}
              />

              {titleReq ? <span className="req-class">*Required field</span> : null}
            </div>

            <div className="form-field">
              Author:
              {!showAddNewAurthor ? (
                <div>
                  <>
                    <CustomDropdown
                      dropdownId="author"
                      menus={authorsList}
                      setParentsAuthor={setAuthorFromDropdown}
                      context="authors"
                      placeholder="Select author"
                    />{" "}
                    or{" "}
                    <span
                      className="add-new-aurthor" 
                      onClick={(e) => setShowAddNewAurthor(!showAddNewAurthor)}
                    >
                      <i className="fas fa-plus" style={{fontSize: "12px"}}></i> Add new
                    </span>
                  </>
                </div>
              ) : (
                <span className="row">
                  <span className="col-xs-7 col-sm-7 col-md-7 col-lg-7  ">
                    <Input
                      placeholder="add new author"
                      readOnly={readOnly}
                      onChange={(e) => {
                        setAuthor(e.target.value);
                      }}
                      style={{ marginTop: "10px" }}
                      value={author}
                    />
                  </span>
                  <span
                    className=" col-xs-2 col-sm-2 col-md-2 col-lg-2 add-icon"
                    id="addIcon"
                    onClick={(e) => {
                      addAuthor({
                        variables: { authorName: author },
                        refetchQueries: [{ query: AUTHORS }],
                      });setShowAddNewAurthor(!showAddNewAurthor);
                    }}
                  >
                    <i className="fas fa-plus"></i>
                  </span>
                  <span
                    className=" col-xs-2 col-sm-2 col-md-2 col-lg-2 add-icon"
                    id="backIcon"
                    onClick={(e) => setShowAddNewAurthor(!showAddNewAurthor)}
                  >
                    <i className="fas fa-arrow-left"></i>
                  </span>

                  {/* <Tooltip placement="bottom" isOpen={addTooltipOpen} target="addIcon" toggle={addToggleTooltip}>
                            Add 
                          </Tooltip>
                          <Tooltip placement="bottom" isOpen={backTooltipOpen} target="backIcon" toggle={backToggleTooltip}>
                            Back
                          </Tooltip> */}
                </span>
              )}
              <div></div>

              {authorReq ? <span className="req-class">*Required field</span> : null}
            </div>

            <div className="form-field">
              Genre:{" "}
              {/* <Input
                placeholder="add genre"
                onClick={(e) => setShowDropdown(true)}
                onChange={(e) => {
                  setGenre(e.target.value);
                }}
              /> */}
              <CustomGenreDropdown
                dropdownId="genre"
                menus={genreList}
                setParentsGenre={setGenreFromDropdown}
                context="genre"
                placeholder="Select genre"
              />
            </div>

            <div className="form-field">
              How was it:{" "}
              <textarea
                placeholder="Short summary"
                style={{ width: "100%" }}
                onChange={(e) => {
                  setDesc(e.target.value);
                }}
              />
            </div>
          </div>
        </ModalBody>
        <ModalFooter>
          <Button
            color="secondary"
            disabled={hasAllMandatoryFields}
            onClick={(e) => {
              closeModal();
              addReview({
                variables: {
                  title: title,
                  author: selectedAuthor,
                  genre: genre,
                  desc: desc,
                },
                refetchQueries: [{ query: GET_REVIEW }],
              });
            }}
          >
            <i className="fas fa-plus"></i> Add
          </Button>
        </ModalFooter>
      </Modal>
    </div>
  );
};

export default AddReviewModal;
