import React, { useState } from 'react';
import './book-list.css';
import {Button} from 'reactstrap';
import AddReviewModal from '../../Utlies/add-review-modals';
import { gql, useQuery } from '@apollo/client';
import RightPanel from '../../Utlies/right-panel/right-panel.js';

const GET_DOGS = gql`
  query GetReview {
    reviews {
        title
        author
        genre
        desc
    }
  }
`;

export default function(){

    const [showModal, setShowModal] = useState(false);
    const {error,loading,data} = useQuery(GET_DOGS);

    console.log("error,loading,data ",error,loading,data);

    function showAddReviewModal(value){
        setShowModal(value);
    }

    function updateReview(){


    }

    function showRightPanel(){
        document.getElementById("mySidepanel").style.width = "40%";
        //document.getElementById("container").style.opacity = "0.5";
    }

    return (
        <div>
            <div className="row header-row">
                <div className="col-xs-4 col-sm-4 col-md-4 col-lg-4 header-text"><h5>Books</h5> </div>
                <div className="col-xs-4 col-sm-4 col-md-4 col-lg-4"> </div>
                <div className="col-xs-3 col-sm-4 col-md-4 col-lg-4">
                    <div className="btn-container"></div><Button onClick={e=>showAddReviewModal(true)}><i className="fas fa-plus"></i> Add review</Button>
                </div>
                
            </div>

            {data && data.reviews && data.reviews.length>0 ? data.reviews.map((book,index)=>{
                return (<div key={index}>
                    <div className="row" >
                        <div className=" book-container" onClick={e=>showRightPanel()}>
                            <div className="">
                                <div className="book-title">
                                    <h5>{book.title}</h5>
                                </div>
                            </div>
                        <div className="row"></div>
                    <div className="row"></div>
                                    
                                        
                    </div>
                </div>
                </div>)
            }):null}

            {showModal ? <AddReviewModal closeModal={showAddReviewModal} updateReview={updateReview}/> : null}
            <RightPanel/>
        </div>
    );
}
