import React, { useState } from 'react';
import './navbar.css';
import CustomPopover from '../../Utlies/popover/popover';

export default function(){
    const [ showPopover , setShowPopover] = useState(false);

    return(
        <>
        <div className="container">
        <ul className="horizontal">
            <div className="row">
                <div className="col-xs-6 col-sm-6 col-md-6 col-lg-6">
                    <div><a href="#home" className="logo">BookLovers</a></div>
                </div>
                <div className="col-xs-6 col-sm-6 col-md-6 col-lg-6">
            
                <li className="custom-nav" onClick={e=>setShowPopover(!showPopover)} ><a href="#news"><i className="fas fa-caret-down" id="account-option-popover"></i></a></li>
                <li className="custom-nav"><div className="join-btn"><a>Join</a></div></li>
                    {/* <li className="custom-nav"><a href="#about">About</a></li>
                    <li className="custom-nav"><a href="#contact">Authors</a></li>
                    <li className="custom-nav"><a href="#news">News</a></li> */}
                    <li className="custom-nav"><a href="#home"><i className="fas fa-home"></i></a></li>

                    <CustomPopover targetId="account-option-popover" showPopover={showPopover} />
                    
                </div>
            </div>

            
                
                
               
                
                
            </ul>
        </div>
            
        </>
    )
}
