import React, { useState, useEffect } from 'react';
import { Button, Modal, ModalHeader, ModalBody, ModalFooter, Input, Tooltip} from 'reactstrap';
import  './add-review-modal.css';
import { gql, useMutation, useQuery } from '@apollo/client';
import CustomDropdown from './dropdown/dropdown';

const ADD_REVIEW = gql`
  mutation AddReview($title:String!, $author:String!, $genre:String, $desc:String){
    addReview(title: $title, author: $author, genre: $genre, desc:$desc){
      title
      author
      genre
      desc
    }
  }
`;

const ADD_AUTHOR= gql`
  mutation AddAuthor($authorName:String!){
    addAuthor(name:$authorName){
      name
    }
  }
`;

const AUTHORS = gql`
  query GetAuthors{
    authors{
      name
    }
    
  }
`

const AddReviewModal = (props) => {
  const {className} = props;

  const [modal, setModal] = useState(true);
  const [title, setTitle] = useState(true);
  const [author, setAuthor] = useState(true);
  const [genre, setGenre] = useState('');
  const [desc, setDesc] = useState(true);
  const [showDropdown, setShowDropdown] = useState(false);
  const [showAddNewAurthor, setShowAddNewAurthor] = useState(false);
  const [readOnly, setReadOnly] = useState(false);

  const [addTooltipOpen, setAddTooltipOpen] = useState(false);
  const [backTooltipOpen, setBackTooltipOpen] = useState(false);

  const addToggleTooltip = () => setAddTooltipOpen(!addTooltipOpen);
  const backToggleTooltip = () => setBackTooltipOpen(!backTooltipOpen);

  const [addReview, { data }] =  useMutation(ADD_REVIEW);
  const [addAuthor, { authordata }] = useMutation(ADD_AUTHOR);
  const [{ error , loading , authorsData }] = useQuery(AUTHORS);

  console.log("authorsData",authorsData);

  const toggle = () => setModal(!modal);

  useEffect(() => {
    if(props.updateReview) props.updateReview();
    
    return () => {}
  }, [data])


  function closeModal(){

    setModal(!modal);

    if(props.closeModal) {
    
        setTimeout(()=>{
            props.closeModal(false);
        },100);    
    }
  }


  return (
    <div>
      
      <Modal isOpen={modal} toggle={toggle} className={className} onClosed={()=>closeModal()}>
        <ModalHeader toggle={toggle} style={{backgroundColor: "#f7f7f7"}}>Add review</ModalHeader>
        <ModalBody>
            <div>
                <div className="form-field">
                    Title: <Input placeholder="add a title" onChange={e=>{setTitle(e.target.value)}}/>
                </div>

                <div className="form-field">
                    Author: 
                    {!showAddNewAurthor?   <div>
                    {authorsData && authorsData.authors ? <> 
                      <CustomDropdown menus={authorsData.authors}/> or <span className="add-new-aurthor" onClick={e=>setShowAddNewAurthor(!showAddNewAurthor)}>Add new</span>
                    </>: null}  
                    </div> : 
                    <span className="row">
                          <span className="col-xs-7 col-sm-7 col-md-7 col-lg-7  ">
                            <Input placeholder="add new author" readOnly={readOnly} onChange={e=>{setAuthor(e.target.value)}} style={{marginTop:"10px"}} value={author}/> 
                          </span>
                          <span className=" col-xs-2 col-sm-2 col-md-2 col-lg-2 add-icon" id="addIcon" onClick={e=>{
                            addAuthor({variables:{ authorName: author }});
                          }
                            
                          }><i className="fas fa-plus"></i></span>
                          <span className=" col-xs-2 col-sm-2 col-md-2 col-lg-2 add-icon" id="backIcon" onClick={e=>setShowAddNewAurthor(!showAddNewAurthor)}><i className="fas fa-arrow-left"></i></span>
                         
                            {/* <Tooltip placement="bottom" isOpen={addTooltipOpen} target="addIcon" toggle={addToggleTooltip}>
                            Add 
                          </Tooltip>
                          <Tooltip placement="bottom" isOpen={backTooltipOpen} target="backIcon" toggle={backToggleTooltip}>
                            Back
                          </Tooltip> */}
                        
                          
                        
                        </span>  }
                  
                    <div>
                      
                    </div>
                </div>

                <div className="form-field">
                    Genre: <Input placeholder="add genre" onClick={e=>setShowDropdown(true)} onChange={e=>{setGenre(e.target.value)}} />
                
                </div>

                <div className="form-field">
                    How was it: <textarea placeholder="Short summary" style={{width:"100%"}} onChange={e=>{setDesc(e.target.value)}}/>
                </div>
              
              
              
            </div>
        </ModalBody>
        <ModalFooter>
            
          <Button color="secondary" onClick={e=>{closeModal(); addReview({ variables: { title: title, author:author ,genre:genre, desc: desc } });}}><i className="fas fa-plus"></i> Add</Button>
        </ModalFooter>
      </Modal>
    </div>
  );
}

export default AddReviewModal;
