import React, {useState} from 'react';
import './account-option-popover.css';
import LoginModal from '../../../Utlies/modals/login/login-sign-up-modal';

export default function AccountOptionPopover(){

    const options=["Settings","Log in"],
    [showLoginModal , setShowLoginModal]= useState(false);

    const optionClicked = (value,option,)=>{

      setShowLoginModal(value);
    }

    var parent = this._reactInternalInstance._currentElement._owner._instance;
    console.log("parent:", parent);
    return(
        <div>

            {options.map((option,index)=> <div key={index } className="acc-option-popover" onClick={e=>optionClicked(true,option)}> {option} </div>)}

            {showLoginModal ? <LoginModal closeModal={optionClicked} context='logIn' />: null}
        </div>
    )
}
