import { gql } from "@apollo/client";

// eslint-disable-next-line
const MessageInput = gql`{
  input MessageInput {
    genre: String
    author: String
  }
}`
//Books componet
// export const GET_REVIEW = gql`
//   query GetReview($genre: String, $author: String) {
//     reviews(genre:$genre, author:$author) {
//         title
//         genre
//         desc
//         imgId
//         author{
//             name
//         }
//     }
//   }
// `;

export const GET_REVIEW = gql`
  query GetReview($query: MessageInput) {
    reviews(query:$query) {
        title
        genre
        desc
        imgId
        author{
            name
        }
    }
  }
`;

export const ADD_REVIEW = gql`
  mutation AddReview(
    $title: String!
    $author: String!
    $genre: String
    $desc: String
    $imgId:String
  ) {
    addReview(title: $title, author: $author, genre: $genre, desc: $desc, imgId: $imgId) {
      title
      author
      genre
      desc
    }
  }
`;

export const ADD_AUTHOR = gql`
  mutation AddAuthor($authorName: String!) {
    addAuthor(name: $authorName) {
      name
    }
  }
`;

export const ADD_USER = gql`
  mutation AddUser($name: String!, $email: String!, $password: String) {
    addUser(name: $name, email: $email, password: $password) {
      name
    }
  }
`;

export const AUTHORS = gql`
  query GetAuthors {
    authors {
      _id
      name
    }
  }
`;
