import React, { useState } from "react";
import "./book-list.css";
import { Button } from "reactstrap";
import AddReviewModal from "../../Utlies/add-review-modals";
import { gql, useQuery } from "@apollo/client";
import RightPanel from "../../Utlies/right-panel/right-panel.js";
import { GET_REVIEW } from "../../gql-helper/queries";
import Loading from "../../Utlies/loader/loader";

export default function () {
  const [showModal, setShowModal] = useState(false);
  const { error, loading, data } = useQuery(GET_REVIEW);

  console.log("error,loading,data ",data);

  function showAddReviewModal(value) {
    setShowModal(value);
  }

  function updateReview() {}

  async function getImgSrc(imgId){

    let imgSrc = '';


    await fetch("https://api.jsonbin.io/b/"+imgId,{
      method: 'GET',
      headers: {
        'Content-Type': 'application/json;charset=utf-8',
        'secret-key':'$2b$10$rhIFVqhUIEQwsbi0CwHLMe6jxkKyBD3otM2McKelyCAyWXJyFWQ7G'
      }
    }).then(async(response)=>{
      let result = await response.json();

      if(result.imgSrc) {
        console.log("okay!!" ,typeof result.imgSrc);
        imgSrc = result.imgSrc;
      }

      console.log("Downloaded!!",result);
    });

    console.log("imgSrc!!",imgSrc.toString());

    return ;
  }

  function showRightPanel() {
    document.getElementById("mySidepanel").style.width = "40%";
    //document.getElementById("container").style.opacity = "0.5";
  }

  return (
    <div className="container">
      <div className="row header-row">
        <div className="col-xs-2 col-sm-2 col-md-2 col-lg-2 header-text">
          <h5>Books</h5>{" "}
        </div>
        <div className="col-xs-2 col-sm-2 col-md-2 col-lg-2 header-text"> </div>
        <div
          className="col-xs-8 col-sm-8 col-md-8 col-lg-8"
          style={{ textAlign: "end" }}
        >
          <div className="btn-container"></div>
          <Button onClick={(e) => showAddReviewModal(true)}>
            <i className="fas fa-plus"></i> Add review
          </Button>
        </div>
      </div>
      {loading ? <Loading /> : null}
      <div className="row">
        {data && data.reviews && data.reviews.length > 0
          ? data.reviews.map((book, index) => {
              return (
                <div
                  className="col-xs-12 col-sm-12 col-md-4 col-lg-4 "
                  key={index}
                >
                  <div className="book-container">
                    <div className="col-xs-4 col-sm-4 col-md-4 col-lg-4" style={{padding:"0px"}}>
                    {book.imgId && getImgSrc(book.imgId) ? <img src={getImgSrc(book.imgId) } width="100%" height="100%"/> : 
                      <img src="https://images.pexels.com/photos/965340/pexels-photo-965340.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=650&w=940" width="100%" height="100%"/>}  


                    </div>
                    <div className="col-xs-9 col-sm-9 col-md-9 col-lg-9" style={{paddingLeft:"0px"}}>
                      <div
                        className="book-description"
                        onClick={(e) => showRightPanel()}
                      >
                        <div className="row" style={{borderBottom:"1px solid #eee"}}>
                          <h5>{book.title}</h5>
                        </div>

                        {book.author && book.author.name ? (
                          <div className="row author-name">
                            <p>By: {" " + book.author.name}</p>
                          </div>
                        ) : null}
                        <div className="row genre">
                          <p>Genre: {" " + book.genre}</p>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              );
            })
          : null}
      </div>

      {showModal ? (
        <AddReviewModal
          closeModal={showAddReviewModal}
          updateReview={updateReview}
        />
      ) : null}
      <RightPanel />
    </div>
  );
}
