import { gql } from "@apollo/client";

//Books componet
export const GET_REVIEW = gql`
  query GetReview {
    reviews {
        title
        author
        genre
        desc
    }
  }
`;

export const ADD_REVIEW = gql`
  mutation AddReview(
    $title: String!
    $author: String!
    $genre: String
    $desc: String
  ) {
    addReview(title: $title, author: $author, genre: $genre, desc: $desc) {
      title
      author
      genre
      desc
    }
  }
`;

export const ADD_AUTHOR = gql`
  mutation AddAuthor($authorName: String!) {
    addAuthor(name: $authorName) {
      name
    }
  }
`;

export const AUTHORS = gql`
  query GetAuthors {
    authors {
      _id
      name
    }
  }
`;
