import React, { useState, useEffect } from 'react';
import {
    InputGroup,
    InputGroupAddon,
    Input,
    Button,
   } from 'reactstrap';
import './search-section.css';

export default function(props){

    const {updateSearchText} = props;

    const changeSearchText=(value) =>  updateSearchText(value)
    
    return(
        <>
            <div className="container">
                <div className="search-section">
                    
                    <span>
                        <InputGroup>
                            <Input placeholder="Search book by title" size="lg" onChange={e=>changeSearchText(e.target.value)}/>
                            <InputGroupAddon addonType="append"><Button> <i className="fas fa-search"></i></Button></InputGroupAddon>
                            
                        </InputGroup>
                    </span>
                </div>
            </div>
        </>
    )
}
