import React, { useState } from 'react';
import { Dropdown, DropdownMenu, DropdownToggle, InputGroup } from 'reactstrap';
import './dropdown.css'

const Example = () => {
  const [dropdownOpen, setDropdownOpen] = useState(false);
  const [testValue, setTestValue] = useState('');
  const [menu,setMenu] = useState(["Value oo","vasdkaf"]);

  const toggle = () => setDropdownOpen(prevState => !prevState);

  return (
    <Dropdown isOpen={dropdownOpen} toggle={toggle}>
      <DropdownToggle
        tag="span"
        data-toggle="dropdown"
        aria-expanded={dropdownOpen} 
      >
         
    {/* <input  type="test" className="dropdown-box" value={testValue} placeholder="please select"/>    */}
    <InputGroup>
        <InputGroupAddon addonType="prepend">
          <InputGroupText>@</InputGroupText>
        </InputGroupAddon>
        <Input  value={testValue} placeholder="please select" />
      </InputGroup>
       
      </DropdownToggle>
      <DropdownMenu>
  {menu.map((value)=><div className="drop-menu" onClick={e=>{console.log(value); setDropdownOpen(false); setTestValue(value)}}>{value}</div>)}
        
      </DropdownMenu>
    </Dropdown>
  );
}

export default Example;
