import React, { useState, useEffect } from "react";
import {
  Modal,
  ModalBody,
} from "reactstrap";

import SignupModal from "../../../components/sign-up/sign-in-form";

export default function LoginSignUpModal(props) {
  const { className, showModal } = props;

  const [modal, setModal] = useState(false);

  const toggle = () => setModal(!modal);

  useEffect(()=>{

    if(showModal) setModal(true)

  },[showModal]);

  function closeModal() {
    setModal(!modal);

    if (props.closeModal) {
      setTimeout(() => {
        props.closeModal(false);
      }, 100);
    }
  }

  return (
    <div>
      <Modal
        isOpen={modal}
        toggle={toggle}
        className={className}
        onClosed={() => closeModal()}
      ></Modal>
      <ModalBody>
        <SignupModal />
      </ModalBody>
    </div>
  );
}
