import React, { useState, useEffect } from "react";

import AddReviewModal from "../../Utlies/add-review-modals";
import RightPanel from "../../Utlies/right-panel/right-panel.js";
import Loading from "../../Utlies/loader/loader";

import { Button } from "reactstrap";
import {  useQuery } from "@apollo/client";
import { GET_REVIEW } from "../../gql-helper/queries";

import "./book-list.css";

function Image(props) {
  const { book } = props;
  const { imgId } = book;
  const [imgSrc, setImgSrc] = useState(
    "https://images.pexels.com/photos/965340/pexels-photo-965340.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=650&w=940"
  );

  useEffect(() => {
    getImgSrc();
    return () => {};
  }, []);

  async function getImgSrc() {

    if (imgId) {
      await fetch("https://api.jsonbin.io/b/" + imgId, {
        method: "GET",
        headers: {
          "Content-Type": "application/json;charset=utf-8",
          "secret-key":
            "$2b$10$rhIFVqhUIEQwsbi0CwHLMe6jxkKyBD3otM2McKelyCAyWXJyFWQ7G",
        },
      }).then(async (response) => {
        let result = await response.json();

        if (result.imgSrc) {
          setImgSrc(result.imgSrc);
        }

      });
    }
  }

  return (
    <>
      <img src={imgSrc} width="100%" height="100%" className="img-container" />
    </>
  );
}

export default function (props) {
  const { searchedText } = props;
  const [showModal, setShowModal] = useState(false);
  const { error, loading, data } = useQuery(GET_REVIEW);
  const [bookReviews, setBookReviews] = useState([]);
  const [headerText, setHeaderText] = useState('Book reviews');

  const showAddReviewModal = (value) => {
    setShowModal(value);
  };

  const showRightPanel = () => {
    document.getElementById("mySidepanel").style.width = "40%";
    //document.getElementById("container").style.opacity = "0.5";
  };
  useEffect(() => {

    let reviews = data && data.reviews && data.reviews.length> 0  ? data.reviews : [];

    console.log("searchedText useEffect",searchedText);
    console.log("reviews useEffect",reviews);

    let filteredReviews = reviews && searchedText? reviews.filter((bookReview) => {
      setHeaderText( "Filtered book reviews");
            return bookReview.title.toLowerCase().includes(searchedText.toLowerCase())
              ? bookReview
              : "";
          })
        : reviews;

        setBookReviews(filteredReviews);

        

    return () => {
      
    };
  }, [searchedText,data]);

  return (
    <div className="container">
      <div className="row header-row">
        <div className="col-xs-2 col-sm-2 col-md-2 col-lg-2 header-text">
      <h5>{headerText}</h5>{" "}
        </div>
        <div className="col-xs-2 col-sm-2 col-md-2 col-lg-2 header-text"> </div>
        <div
          className="col-xs-8 col-sm-8 col-md-8 col-lg-8"
          style={{ textAlign: "end" }}
        >
          <div className="btn-container"></div>
          <Button onClick={(e) => showAddReviewModal(true)}>
            <i className="fas fa-plus"></i> Add review
          </Button>
        </div>
      </div>
      {loading ? <Loading /> : null}
      <div className="row">
        {bookReviews && bookReviews.length > 0
          ? bookReviews.map((book, index) => {
              return (
                <div
                  className="col-xs-12 col-sm-12 col-md-4 col-lg-4 "
                  key={index}
                >
                  <div className="book-container">
                    <div
                      className="col-xs-4 col-sm-4 col-md-4 col-lg-4"
                      style={{ padding: "0px" }}
                    >
                      <Image book={book} />
                    </div>
                    <div
                      className="col-xs-9 col-sm-9 col-md-9 col-lg-9"
                      style={{ paddingLeft: "0px" }}
                    >
                      <div
                        className="book-description"
                        onClick={(e) => showRightPanel()}
                      >
                        <div
                          className="row"
                          style={{ borderBottom: "1px solid #eee" }}
                        >
                          <h5>{book.title}</h5>
                        </div>

                        {book.author && book.author.name ? (
                          <div className="row author-name">
                            <p>By: {" " + book.author.name}</p>
                          </div>
                        ) : null}
                        <div className="row genre">
                          <p>Genre: {" " + book.genre}</p>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              );
            })
          : null}
      </div>

      {showModal ? (
        <AddReviewModal
          closeModal={showAddReviewModal}
        />
      ) : null}
      <RightPanel />
    </div>
  );
}
