import React, { useState } from 'react';
import { Button, Modal, ModalHeader, ModalBody, ModalFooter, InputGroup, Input} from 'reactstrap';

const AddReviewModal = (props) => {
  const {
    buttonLabel,
    className
  } = props;

  const [modal, setModal] = useState(true);

  const toggle = () => setModal(!modal);

  function addReview(){

    closeModal();

  }

  function closeModal(){
    setModal(!modal);

    if(props.closeModal) {
    
        setTimeout(()=>{
            props.closeModal(false);
        },100);    
    }
  }

  return (
    <div>
      
      <Modal isOpen={modal} toggle={toggle} className={className} onClosed={()=>closeModal()}>
        <ModalHeader toggle={toggle}>Modal title</ModalHeader>
        <ModalBody>
            <InputGroup>
              <Input placeholder="Search book by title"/>
              
              
            </InputGroup>
        </ModalBody>
        <ModalFooter>
            
          <Button color="secondary" onClick={e=>addReview()}>add</Button>
        </ModalFooter>
      </Modal>
    </div>
  );
}

export default AddReviewModal;
