import React, { useState, useEffect, useMemo } from "react";
import "./App.css";
import BookSection from "./components/books-section/index";
import Navbar from "./components/navbar/navbar";
import SearchSection from "./components/search-section/search-section";
import { FilterContext } from "./context-helper/context-helper";
import SignInForm from './components/sign-up/sign-in-form'

function App() {
  const [searchedText, setSearchedText] = useState("");
  const [rootGenre, setRootGenreFilter] = useState("");
  const [rootAuthorFilter, setRootAuthorFilter] = useState("");

  const updateSearchText = (text) => {
    setSearchedText(text);
  };

  const authorFilter = useMemo(() => ({rootAuthorFilter, setRootAuthorFilter}), [rootAuthorFilter, setRootAuthorFilter]) 

  return (
    <div className="App">
       <div className="Navbar">
          <Navbar />
        </div>

      <FilterContext.Provider value={{rootGenre, setRootGenreFilter, authorFilter}}>

        <header className="App-header">
          <SearchSection updateParentSearchText={updateSearchText} />
        </header>
        <BookSection searchedText={searchedText} />

      </FilterContext.Provider>

    </div>
  );
}

export default App;
