import React, { useState } from 'react';
import { Dropdown, DropdownMenu, DropdownToggle, FormControl } from 'reactstrap';
import './dropdown.css'

const Example = () => {
  const [dropdownOpen, setDropdownOpen] = useState(false);
  const [testValue, setTestValue] = useState(false);

  const toggle = () => setDropdownOpen(prevState => !prevState);

  return (
    <Dropdown isOpen={dropdownOpen} toggle={toggle}>
      <DropdownToggle
        tag="span"
        data-toggle="dropdown"
        aria-expanded={dropdownOpen} 
      >
         <FormControl autoFocus className="mx-3 my-2 w-auto" placeholder="Type to filter..." onChange={(e) => setValue(e.target.value)} value={value}/>
             {/* <input type="text" className="dropdown-box" value={testValue} placeholder="please select"/>    */}
        
      </DropdownToggle>
      <DropdownMenu>
        <div onClick={e=>setTestValue(e.target.value)}>value 1</div>
        <div onClick={e=>setTestValue(e.target.value)}>value 2</div>
        <div onClick={e=>setTestValue(e.target.value)}>value 3</div>
      </DropdownMenu>
    </Dropdown>
  );
}

export default Example;
